import React, {useState} from "react"
import GGT from "./GGT"


// For this project we need to use linear-gradient() CSS function 
// creates an image consisting of a progressive transition between two or more colors along a straight line.

// Have a component that has 2 or 3 inputs and a number input to represent the angle. This will be like a form 

// Have a component that previews that displays the current gradient

// Generate cross-browser CSS code in a text box using <textarea>

export default function App() {
    /// This is to display the current gradient.

    const[formData, setFormData] = useState(
        "linear-gradient(50deg, #FFFF00 , #000000)"
        )

    // This is to save the the users input gradient data.
    const[gradient,setGradient] = useState({
        angle:"50deg",
        color1:"#FFFF00",
        color2:"#000000"

    })

    // Making template for cross browser CSS Code
    const[generatedCss,SetGeneratesCss] = useState(
        `background: linear-gradient(50deg, #FFFF00 , #000000); 
        -moz-background: linear-gradient(50deg, #FFFF00 , #000000); 
        -webkit: linear-gradient(50deg, #FFFF00 , #000000)`
    )

    function handleChange(event) {
        const{name,value} = event.target
        setGradient(prevGradient => {
            return {
                ...prevGradient,
                [name]:value
            }
        })
    }


    function handleSubmit(event) {
        event.preventDefault()
        setGradient

    }
    return (
        <div>
            <GGT>
                <form onSubmit={handleSubmit}>
                    <input name="color1" value={gradient.color1} type="text" placeholder="Color 1" onChange={handleChange}></input>
                    <input name="color2" value={gradient.color2} type="text" placeholder="Color 2" onChange={handleChange}></input>
                    <input name="angle" value={gradient.angle} type="number" placeholder="Place Angle Number" onChange={handleChange}></input>
                    <button>Generate CSS Gradient Code</button>
                </form>
            </GGT>

            <GGT>
                <div>
                    <div style={{background: formData, height:300, width:300}}>

                    </div>
                </div>
            </GGT>
            <GGT>
                <div style={{
                    height:300,
                    width:300,
                    background:`linear-gradient(${gradient.angle}, ${gradient.color1}, ${gradient.color2})`
                    }}>

                </div>
            </GGT>

            <GGT>
                <form>
                    <textarea style={{heigh:100, width:150}} readOnly-value={generatedCss}>
                        {generatedCss}
                    </textarea>
                </form>
            </GGT>

        </div>




    )

}
